#file must be sourced

logger "INFO: Creating DB presets for $DB (if necessary)"

$MYSQL "
CREATE TABLE IF NOT EXISTS filter_presets (
 id_preset int(11) NOT NULL AUTO_INCREMENT,
 selected_tool varchar(255) NOT NULL,
 URL longtext NOT NULL,
 default_preset int(1) NOT NULL DEFAULT 0,
 short_name varchar(255) NOT NULL,
 description longtext,
 PRIMARY KEY (id_preset),
 UNIQUE (selected_tool,short_name)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

"

# TODO remove in some time
$MYSQL "DROP TABLE IF EXISTS filters_presets;"

###############################################################################
# Presets for Local Deployment

#common settings for most filters in case we are NOT in the vagrant VM
if [ ! -d  "/vagrant" ] ; then
  filter_common="&uobsr=1&minexetime=50&maxexetime=5000&filters[]=valid&filters[]=filters"
  filter_ml="&bench_type[]=HiBench"
#for vagrant allow more values
else
  filter_common="&uobsr=1&minexetime=1&maxexetime=5000"
  filter_ml="&bench_type[]=HiBench"
fi

filter_HiBench_Terasort_8d_2015="$filter_common&bench_type[]=HiBench&bench[]=terasort&datanodes[]=8&datefrom=2015/01/01"
filter_HiBench_Terasort_WordCount_Sort_8d_2015="$filter_common&bench_type[]=HiBench&bench[]=sort&bench[]=terasort&bench[]=wordcount&datanodes[]=8&datefrom=2015/01/01"


# Delete them first, as if a name changes, then the old one is not deleted
$MYSQL "DELETE FROM filter_presets;"

